package com.jerry.framework.util;

import sun.misc.BASE64Decoder;
import sun.misc.BASE64Encoder;

import java.io.*;
import java.util.UUID;

public class Base64AndPictureUtil {

	public static void main(String[] args) {
		//String strImg = GetImageStr();
		//System.out.println(strImg);
	    String strImg = "";
		String imgSaveUrl = "d:/base64pictest";
		generateImage(strImg, imgSaveUrl);
	}

    /**
     * 图片转化成base64字符串 将图片文件转化为字节数组字符串，并对其进行Base64编码处理
     * @param imgUrl 待处理的图片路径
     * @return
     */
	public static String getImageStr(String imgUrl) {
		InputStream in = null;
		byte[] data = null;
		// 读取图片字节数组
		try {
			in = new FileInputStream(imgUrl);
			data = new byte[in.available()];
			in.read(data);
			in.close();
		} catch (IOException e) {
			e.printStackTrace();
		}
		// 对字节数组Base64编码
		BASE64Encoder encoder = new BASE64Encoder();
		return encoder.encode(data);// 返回Base64编码过的字节数组字符串
	}

    /**
     * base64字符串转化成图片 对字节数组字符串进行Base64解码并生成图片
     * @param request
     * @param imgStr base64图片编码
     * @param relroot 存储路径
     * @return
     */

    public static void generateImage(String imgStr,String relroot) {
        String newName = reName("")+".png";
        File targetFile = new File(relroot, newName);
        if (!targetFile.getParentFile().exists()) {
            targetFile.getParentFile().mkdirs();
        }
        BASE64Decoder decoder = new BASE64Decoder();
        try {
            // Base64解码
            byte[] b = decoder.decodeBuffer(imgStr);
            for (int i = 0; i < b.length; ++i) {
                if (b[i] < 0) {// 调整异常数据
                    b[i] += 256;
                }
            }
            // 生成jpeg图片
            OutputStream out = new FileOutputStream(targetFile);
            out.write(b);
            out.flush();
            out.close();
        } catch (Exception e) {
        }
    }

    public static byte[] generateImage(String imgStr) {
        BASE64Decoder decoder = new BASE64Decoder();
        byte[] data = {};
        try {
            // Base64解码
            data = decoder.decodeBuffer(imgStr);
            for (int i = 0; i < data.length; ++i) {
                if (data[i] < 0) {// 调整异常数据
                    data[i] += 256;
                }
            }
        } catch (Exception e) {
        }
        return data;
    }

    private static String reName(String name) {
        String fileName = (UUID.randomUUID()+"").split("-")[0]+(System.currentTimeMillis()+Math.round(Math.random()*10000));
        if (name.indexOf(".") != -1) {
            fileName += name.substring(name.lastIndexOf("."));
        }
        return fileName;
    }
}